import React, { useState } from "react";
import {scroll, banner1, banner2, banner3} from '../assets/export'
import '../index.css'
import {AiFillCaretLeft, AiFillCaretRight } from 'react-icons/ai'

const Carousel = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    const handlePrevClick = () => {
      setActiveIndex(activeIndex - 1) 
    };

    const handleNextClick = () => {
      setActiveIndex(activeIndex + 1) 
    };
    return (
      <>
          <section>
            <div className="carousel">
              <AiFillCaretLeft className="carousel-button prev"
                onClick={handlePrevClick} 
              ></AiFillCaretLeft>
              <AiFillCaretRight className="carousel-button next"></AiFillCaretRight>
              <ul>
                <li className="slide" data-active>
                  <img src={banner1} alt="banner01" />   
                </li>   
                <li className="slide">
                  <img src={banner2} alt="banner01" />   
                </li>   
                <li className="slide">
                  <img src={banner3} alt="banner01" />   
                </li>   
              </ul>   
            </div>
          </section>  
      </>
    )
};

export default Carousel;
